mapper.sql:
FROM 	monit S
WHERE	1=1

	AND S.id = #{id}


	AND S.system_id = #{system_id}

mapper.insert:
INSERT INTO monit
(
	id
	, system_id
	, node_type
	, region
	, node_id
	, node_name
	, private_ip
	, private_port
	, action
	, status
	, message
	, registered_at
)
VALUES
(
	#{id}
	, #{systemId}
	, #{nodeType}
	, #{region}
	, #{nodeId}
	, #{nodeName}
	, #{privateIp}
	, #{privatePort}
	, #{action}
	, #{status}
	, #{message}
	, #{registeredAt}
)
on DUPLICATE key update
	system_id = #{systemId}
	, node_type = #{nodeType}
	, region = #{region}
	, node_id = #{nodeId}
	, node_name = #{nodeName}
	, private_ip = #{privateIp}
	, private_port = #{privatePort}
	, action = #{action}
	, status = #{status}
	, message = #{message}
	, registered_at = #{registeredAt}
