mapper.sql:
FROM 	rule R
WHERE	1=1

	AND R.id = #{id}


	AND R.system_id = #{system_id}

mapper.insert:
INSERT INTO rule
(
	id
	, system_id
	, name
	, description
	, node_type
	, session
	, max_instances
	, region
	, updated_at
	, registered_at
)
VALUES
(
	#{id}
	, #{systemId}
	, #{name}
	, #{description}
	, #{nodeType}
	, #{session}
	, #{maxInstances}
	, #{region}
	, #{updatedAt}
	, #{registeredAt}
)
on DUPLICATE key update
	system_id = #{systemId}
	, name = #{name}
	, description = #{description}
	, node_type = #{nodeType}
	, session = #{session}
	, max_instances = #{maxInstances}
	, region = #{region}
	, updated_at = #{updatedAt}
	, registered_at = #{registeredAt}
